CREATE FUNCTION public.alter_table_drop_column_if_exists(text, text, text) RETURNS boolean
    LANGUAGE plpgsql
    AS $_$
	DECLARE
		p_namespace alias for $1;
		p_table     alias for $2;
		p_field     alias for $3;
		v_row       record;
		v_query     text;
	BEGIN
