--
-- Name: calcul_cle_nir(text); Type: FUNCTION; Schema: public; Owner: webrsa
--

CREATE FUNCTION public.calcul_cle_nir(text) RETURNS text
    LANGUAGE plpgsql IMMUTABLE STRICT
    AS $_$
	DECLARE
		p_nir text;
		cle text;
		correction BIGINT;

	BEGIN
		correction:=0;
		p_nir:=$1;

		IF NOT p_nir ~ '^[0-9]{6}(A|B|[0-9])[0-9]{6}$' THEN
			RETURN NULL;
		END IF;

		IF p_nir ~ '^.{6}(A|B)' THEN
			IF p_nir ~ '^.{6}A' THEN
				correction:=1000000;
			ELSE
				correction:=2000000;
			END IF;
			p_nir:=regexp_replace( p_nir, '(A|B)', '0' );
		END IF;

		cle:=LPAD( CAST( 97 - ( ( CAST( p_nir AS BIGINT ) - correction ) % 97 ) AS VARCHAR(13)), 2, '0' );
		RETURN cle;
	END;
$_$;


