--
-- Name: corrige_personnes_referents_dfdesignation(); Type: FUNCTION; Schema: public; Owner: webrsa
--

CREATE FUNCTION public.corrige_personnes_referents_dfdesignation() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
	v_personnes record;
	v_select_query text;
	v_personnes_referents record;
	v_update_query text;
BEGIN
	FOR v_personnes IN
		SELECT personnes_referents.personne_id, COUNT(*)
			FROM personnes_referents
			WHERE personnes_referents.dfdesignation IS NULL
			GROUP BY personnes_referents.personne_id
			HAVING COUNT(*) > 1
			ORDER BY personnes_referents.personne_id

	LOOP
		RAISE NOTICE  'Traitement de la personne d'' id %', v_personnes.personne_id;

		v_select_query := 'SELECT
			*,
			(
				SELECT dddesignation
					FROM personnes_referents AS personnes_referents_svts
					WHERE
						personnes_referents.personne_id = personnes_referents_svts.personne_id
						AND (
							personnes_referents.dddesignation < personnes_referents_svts.dddesignation
							OR (
								personnes_referents.dddesignation = personnes_referents_svts.dddesignation
								AND personnes_referents.id < personnes_referents_svts.id
							)
						)
					ORDER BY personnes_referents_svts.dddesignation ASC, personnes_referents_svts.id ASC
					LIMIT 1
			) AS "next_dddesignation"
			FROM personnes_referents
			WHERE personne_id = ' || v_personnes.personne_id || '
			ORDER BY dddesignation ASC, id ASC;';
		RAISE NOTICE  'Requête de sélection: %', v_select_query;

		FOR v_personnes_referents IN EXECUTE v_select_query
		LOOP
			IF v_personnes_referents.dfdesignation IS NULL AND v_personnes_referents.next_dddesignation IS NOT NULL THEN
				v_update_query := 'UPDATE personnes_referents SET dfdesignation = ''' || v_personnes_referents.next_dddesignation || ''' WHERE personnes_referents.id = ' || v_personnes_referents.id || ';';
				RAISE NOTICE  '%', v_update_query;
				EXECUTE v_update_query;
			END IF;
		END LOOP;
	END LOOP;
END;
$$;


