--

CREATE FUNCTION public.format_personne(text) RETURNS text
    LANGUAGE plpgsql STRICT
    AS $_$
	DECLARE
		st text;

	BEGIN
		IF ($1 IS NOT NULL) THEN
			-- On transforme les caractères accentués en majuscule - les caractères .,; deviennent des espaces
			st:=translate($1,'aàäâeéèêëiïîoôöuùûücçñAÀÄÂEÉÈÊËIÏÎOÔÖUÙÛÜCÇÑ.",;_','AAAAEEEEEIIIOOOUUUUCCNAAAAEEEEEIIIOOOUUUUCCN ''  -'); 
			-- On passe en majuscule
			st:=upper(st);
			 -- On purge les caractères non autorisés et on trim
			st:=regexp_replace(st, '[^A-Z'' -]', '', 'g');
			st:=regexp_replace(st,' [ ]*', ' ', 'g');	
			st:=regexp_replace(st,'[ ]*-[ ]*', '-', 'g');	 
			st:=regexp_replace(st,'-[-]*', '-', 'g');	
			st:=regexp_replace(st,'[ ]*''[ ]*', '''', 'g');	
			st:=regexp_replace(st,'''['']*', '''', 'g');	
			st:=TRIM(BOTH ' ' FROM st);
			 -- Gestion des codes SP SPC, des X et des chaines vides
			IF (st = 'SP' OR st = 'SPC' OR st = 'X')
				THEN st:='-';
			END IF;	
			IF (TRIM( BOTH 'X ' FROM st)='')
				THEN st:='-';
			END IF;
			IF (st='')
				THEN st:='-';
			END IF;
		ELSE
			-- Gestion des champs NULL
			st:='-';
		END IF;
		RETURN st;
	END;
$_$;


