--
-- Name: format_string_webrsa(); Type: FUNCTION; Schema: public; Owner: webrsa
--

CREATE FUNCTION public.format_string_webrsa() RETURNS integer
    LANGUAGE plpgsql STRICT
    AS $$
	BEGIN
		-- Mise à jour sur la table personnes (nom, ... -> en majuscules...)
		UPDATE personnes SET nom = public.format_personne(nom),
			prenom = public.format_personne(prenom),
			nomnai = public.format_personne(nomnai),
			prenom2 = public.format_personne(prenom2),
			prenom3 = public.format_personne(prenom3);
		-- Formattage du NIR sur 13 caractères
		UPDATE personnes SET nir = regexp_replace(nir, ' ', '', 'g') WHERE nir IS NOT NULL;
		UPDATE personnes SET nir = NULL WHERE nir ='';
		UPDATE personnes SET nir = SUBSTRING(nir FROM 1 FOR 13) WHERE LENGTH(nir) > 13;
		-- Mise à jour de la table dossiers
		UPDATE dossiers SET matricule = NULL WHERE matricule = '000000000000000';
		-- Transformation du MLE en MME
		UPDATE personnes SET qual = 'MME' WHERE qual ='MLE';	
		RETURN 0;
	END;
$$;


