--
-- Name: nir_correct(text); Type: FUNCTION; Schema: public; Owner: webrsa
--

CREATE FUNCTION public.nir_correct(text) RETURNS boolean
    LANGUAGE plpgsql
    AS $_$
	DECLARE
		p_nir text;

	BEGIN
		p_nir:=$1;

		RETURN (
			CHAR_LENGTH( TRIM( BOTH ' ' FROM p_nir ) ) = 15
			AND (
				public.cakephp_validate_ssn( p_nir, null, 'fr' )
				AND public.calcul_cle_nir( SUBSTRING( p_nir FROM 1 FOR 13 ) ) = SUBSTRING( p_nir FROM 14 FOR 2 )
			)
		);
	END;
$_$;


