--
-- Name: nir_correct13(text); Type: FUNCTION; Schema: public; Owner: webrsa
--

CREATE FUNCTION public.nir_correct13(text) RETURNS boolean
    LANGUAGE plpgsql IMMUTABLE
    AS $_$
	DECLARE
		p_nir text;
	BEGIN
		p_nir:=$1;

		IF p_nir IS NULL THEN
			RETURN false;
		END IF;

		RETURN (
			CHAR_LENGTH( TRIM( BOTH ' ' FROM p_nir ) ) >= 13
			AND (
				public.cakephp_validate_ssn( SUBSTRING( p_nir FROM 1 FOR 13 ) || public.calcul_cle_nir( SUBSTRING( p_nir FROM 1 FOR 13 ) ), null, 'fr' )
			)
		);
	END;
$_$;


ALTER FUNCTION public.nir_correct13(text) OWNER TO webrsa;

