-- Name: rename_sequence_ifexists(text, text); Type: FUNCTION; Schema: public; Owner: webrsa
--

CREATE FUNCTION public.rename_sequence_ifexists(p_namefrom text, p_nameto text) RETURNS boolean
    LANGUAGE plpgsql
    AS $$
	DECLARE
		v_row   record;
		v_query text;
	BEGIN
		SELECT 1 INTO v_row
			FROM pg_class c
			WHERE c.relname = p_namefrom || '_id_seq' AND c.relkind = 'S';
		IF FOUND THEN
			RAISE NOTICE 'Upgrade sequence %_id_seq - rename to %_id_seq', p_namefrom, p_nameto;
			v_query := 'ALTER TABLE ' || p_namefrom || '_id_seq RENAME TO ' || p_nameto || '_id_seq;';
			EXECUTE v_query;
			return 't';
		else
			RAISE NOTICE 'Sequence %_id_seq not found', p_namefrom;
			RETURN 'f';
		end if;
	END;
$$;


