--
-- Name: webrsa_historiquesdroits_consolidation(); Type: FUNCTION; Schema: public; Owner: webrsa
--

CREATE FUNCTION public.webrsa_historiquesdroits_consolidation() RETURNS void
    LANGUAGE plpgsql
    AS $$
	DECLARE
		v_row record;
		v_query text;
		v_count integer;
	BEGIN
		v_count := 0;

		FOR v_row IN
			SELECT
					histo.id,
					( histosvt.created - INTERVAL '1 day' ) AS modified
				FROM
					historiquesdroits AS histo,
					historiquesdroits AS histosvt
				WHERE
					histosvt.id = (
						SELECT h.id
							FROM historiquesdroits AS h
							WHERE
								h.id <> histo.id
								AND h.personne_id = histo.personne_id
								AND h.created::DATE >= histo.created::DATE
							ORDER BY h.created ASC
							LIMIT 1
					)
					AND ( histosvt.created - INTERVAL '1 day' )::DATE > histo.modified::DATE
		LOOP
			v_query := 'UPDATE historiquesdroits SET modified = ''' || v_row.modified || ''' WHERE id = ''' || v_row.id || ''';';
			RAISE NOTICE '%', v_query;
			EXECUTE v_query;
			v_count := v_count + 1;
		END LOOP;
		RAISE NOTICE '% enregistrements traités', v_count;
	END;
$$;


ALTER FUNCTION public.webrsa_historiquesdroits_consolidation() OWNER TO webrsa;

--
-- Name: webrsa_historiquesdroits_dedoublonnage(); Type: FUNCTION; Schema: public; Owner: webrsa
--

CREATE FUNCTION public.webrsa_historiquesdroits_dedoublonnage() RETURNS void
    LANGUAGE plpgsql
    AS $$
	DECLARE
		v_personne record;
		v_row record;
		v_query text;
		v_count integer;
		v_toppersdrodevorsa char;
		v_etatdosrsa char;
	BEGIN
		v_count := 0;

		FOR v_personne IN
			SELECT
					DISTINCT personne_id
				FROM historiquesdroits
		LOOP
			RAISE NOTICE 'Traitement de le personne %', v_personne.personne_id;

			v_toppersdrodevorsa := NULL;
			v_etatdosrsa := NULL;

			FOR v_row IN
				SELECT
						historiquesdroits.id AS id,
						historiquesdroits.toppersdrodevorsa AS toppersdrodevorsa,
						historiquesdroits.etatdosrsa AS etatdosrsa
					FROM historiquesdroits
					WHERE
						historiquesdroits.personne_id = v_personne.personne_id
					ORDER BY historiquesdroits.created ASC
			LOOP
				IF (
					-- On ne vient pas de rentrer de la boucle de l'utilisateur
					NOT ( v_toppersdrodevorsa IS NULL AND v_etatdosrsa IS NULL )
					-- On a la même valeur pour toppersdrodevorsa
					AND (
						v_row.toppersdrodevorsa = v_toppersdrodevorsa
						OR ( v_row.toppersdrodevorsa IS NULL AND v_toppersdrodevorsa IS NULL )
					)
					-- On a la même valeur pour etatdosrsa
					AND v_row.etatdosrsa = v_etatdosrsa
				) THEN
					v_query := 'DELETE FROM historiquesdroits WHERE id = ''' || v_row.id || ''';';
					RAISE NOTICE '%', v_query;
					EXECUTE v_query;
				END IF;

				RAISE NOTICE 'v_toppersdrodevorsa % v_etatdosrsa %', v_toppersdrodevorsa, v_etatdosrsa;

				v_toppersdrodevorsa := v_row.toppersdrodevorsa;
				v_etatdosrsa := v_row.etatdosrsa;

				v_count := v_count + 1;
			END LOOP;
		END LOOP;

		RAISE NOTICE '% enregistrements traités', v_count;
	END;
$$;


ALTER FUNCTION public.webrsa_historiquesdroits_dedoublonnage() OWNER TO webrsa;

